import React from 'react';
class Header extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state={time: new Date()};
    }
    
    componentDidMount() {
        this.SetTimer = setInterval(
            () => this.runTime(), 1000
        );
    }

    componentWillUnmount() {
        clearInterval(this.SetTimer);
    }
    
    runTime() {
        this.setState({
          date: new Date()
        });
      }

    render() {
        return (
        <div class="hroot">
            <div class="hleft">Welcome to Dummy!</div>
            <div class="hright">Time is now: {this.state.time.toLocaleTimeString()}</div>
        </div>
        );
    }
}
export default Header;
