import React from 'react';
import App from './App';
import ReactDOM from 'react-dom';
class Forms extends React.Component
{
  constructor(props)
  {
    super(props);
    this.state = {name: ''};
    this.getVal = this.getVal.bind(this);
    this.subVal = this.subVal.bind(this);
  }
  getVal(event)
  {
    this.setState({name: event.target.value});
    document.write("Event: " + event + "</br>Target: " + event.target + "</br>Value" + event.target.value);
  }
  subVal(event)
  {
    alert("Hi "+this.state.name+"!");
    ReactDOM.render(<App />, document.getElementById('root'));
    event.preventDefault();
  }
  render()
  {
    return(
      <form>
        Full Name: <input type="text" value={this.state.value} onChange={this.getVal} />
        E-Mail ID: <input type="text" value={this.state.value} onChange={this.getVal} />
        Password: <input type="text" value={this.state.value} onChange={this.getVal} />
        <br />
        <button onClick={this.subVal}>Submit</button>
      </form>
    );
  }
}
export default Forms;
